workflow-item.script:
Server.log("Parsing cluster response for cluster " + id);

const cluster = JSON.parse(clusterResponse);
const clusterSchema = ["clustergroup", 
        "status", 
        "version", 
        "nodesize", 
        "platform", 
        "controller", 
        "description", 
        "provisioner", 
        "storageclass", 
        "workernodecount", 
        "controlplanereplicas"];


resultObj = DynamicTypesManager.makeObject("TMC", "Cluster", id, id, clusterSchema);

if (!!cluster) {
    Server.log('Creating cluster object with values: ' + clusterResponse);
    for (var i=0; i < clusterSchema.length; i++) {
        var _propname = clusterSchema[i];
        Server.log("Setting cluster property: " + _propname + ' with value: ' + cluster[_propname]);
        setPropertyIfAvailable(_propname);
    }
}

function setPropertyIfAvailable(propertyName) {
    if (cluster[propertyName] !== undefined && cluster[propertyName] !== null) {
        var propValue = cluster[propertyName].toString();
        //resultObj.setProperty(propertyName, JSON.stringify(propValue));
        resultObj.setProperty(propertyName, propValue);
    }
}
Server.log("Cluster object processing completed " + id);
