workflow-item.script:
const cluster = JSON.parse(clusterResponse);
Server.log("Parsing cluster response for cluster " + id)
const clusterSchema = ["id", 
        "name", 
        "clustergroup", 
        "highavailability", 
        "status", 
        "version", 
        "nodesize", 
        "platform", 
        "controller", 
        "description", 
        "provisioner", 
        "storageclass", 
        "workernodecount", 
        "controlplanereplicas"];

if (!!cluster) {
    Server.log('Creating cluster object with values: ' + clusterResponse)
    resultObj = DynamicTypesManager.makeObject("Tanzu Mission Control", "Cluster", id, cluster["name"], clusterSchema);
    for (var i=0; i < clusterSchema.length; i++) {
        setPropertyIfAvailable(clusterSchema[i]);
    }
}
function setPropertyIfAvailable(propertyName) {
    if (cluster[propertyName] !== undefined && cluster[propertyName] !== null) {
        Server.log("Setting cluster property: " + propertyName)
        resultObj.setProperty(propertyName, JSON.stringify(cluster[propertyName]));
    }
}
